package Tree;import Temp.Temp;import java.util.LinkedList;public class EXP extends Stm {    public Exp1 exp;    public EXP(Exp1 e) {        exp=e;    }    public LinkedList<Exp1> kids() {        LinkedList<Exp1> kids = new LinkedList<Exp1>();        kids.addFirst(exp);        return kids;    }    public Stm build(LinkedList<Exp1> kids) {        return new EXP(kids.getFirst());    }    public void accept(IntVisitor v, int d) {        v.visit(this, d);    }    public Temp accept(CodeVisitor v) {        v.visit(this);        return null;    }    public <R> R accept(ResultVisitor<R> v) {        return v.visit(this);    }}